                putMyVar('分集起', total1);
                putMyVar('分集终', total2);
                refreshPage(false);
                return 'hiker://empty'
            }, total1, total2),
            col_type: 'scroll_button'
        });
    }
    for (var j = star - 1; j < end; j++) {
        playLists()
    }
    d.push({
        col_type: "line"
    })
    var yema = Math.ceil(end / page_number);
    d.push({
        title: "““”” <small><small><font color='grey'>" +
            yema + ' / ' + total +
            "</font> </small></small>",
        col_type: "text_center_1",
        extra: {
            lineVisible: false
        }
    })
}
setResult(d)

